        public int findindex(string text )
        {
            int reternrt = 0;
            foreach (string item in ayat)
            {
                if (item.Contains(text))
                {
                    reternrt = Array.IndexOf(ayat, item);
                    break;
                }
                else
                {
                    reternrt = - 1;
                }
               
            }
            return reternrt;
        }

